package coding;

import java.util.*;

public class Num4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/* 11022 */

		Scanner sc = new Scanner(System.in);

		int T = sc.nextInt();

		for (int i = 1; i <= T; i++) {

			int a = sc.nextInt();
			int b = sc.nextInt();

			System.out.println("Case #" + i + ": " + a + " + " + b + " = " + (a + b));
		}

	}

}
